INSERT INTO dbo.orden_trabajo( id, num_ticket, num_ord_trab, empresa_id, ct_sistema, ct_servicio, descripcion, fecha_inicio, fecha_termino, hora_termino, activo, fecha_creacion, cliente_id, tecnico_id, detalle_orden_trabajo_id, solucion, solucion_tecnica, estado ) VALUES ( 414, 17725, 3268, 2, 1000, 1007, 'Le informo que la puerta 1F113  que se encuentra del lado sur del arribo internacional se desarma continuamente la hoja izquierda, cada vez que se hace uso de la puerta.', '2016-03-29 13:56:00.0000000', '2016-03-31 09:00:00.0000000', null, 1, null, 17, 1, null, 'Se realizó la inspección a la pueta 1F113 encontrándola con alarma  delay egress l, debido a que la platina de la cerradura magnética no magnetiza uniformemente. Se realiza la calibración y se deja en normal funcionamiento desde del 29-03-2016 (día de la calibración), hasta hoy 04 de Abril que se le ha hecho seguimiento y ha funcionado normalmente', 'Se realizó la inspección a la pueta encontrándola en delay egress, se realizaron pruebas y se observa que el polato de el mocklog no se pega uniformemente ya que se encuentra mas abajo, se realizó un ajuste al plato a la posición correcta y se deja en normal funcionamiento. A este ticket se le realizó un seguimiento desde del 29-03-2016, pra observar la manipulación de esta puerta y encontrarle el daño, ya que su daño era constante.', 0 ); 
