INSERT INTO dbo.orden_trabajo( id, num_ticket, num_ord_trab, empresa_id, ct_sistema, ct_servicio, descripcion, fecha_inicio, fecha_termino, hora_termino, activo, fecha_creacion, cliente_id, tecnico_id, detalle_orden_trabajo_id, solucion, solucion_tecnica, estado ) VALUES ( 475, 26, 3453, 2, 1002, 1008, 'Las cámaras S-C050, S-C170A, S-C170B se encuentran fuera de servicio.', '2016-05-11 07:50:00.0000000', '2016-05-11 11:30:00.0000000', null, 1, null, 8, 1, null, 'Se coordinó con el personal de ADC & HAS departamento eléctrico y técnicos de Elyte, para la revisión del cableado de alimentación de las cámaras ya que anteriormente se detectó una activación del breaker de protección. Se revisa la acometida encontrando que se derivaron de la acometida de las cámaras 3 tomas para el taller del Hangar de Tame, la cual cuando se conectaban equipos para el taller consumía más amperaje y activaba el breaker de protección y habría el circuito por lo cual se apagaban las cámaras. El persona de ADC & HAS corta la derivación de la acometida y las cámaras quedan operativas.', 'Se coordinó con el personal de ADC & HAS departamento eléctrico y técnicos de Elyte, para la revisión del cableado de alimentación de las cámaras ya que anteriormente se detectó una activación del breaker de protección. Se revisa la acometida encontrando que se derivaron de la acometida de las cámaras 3 tomas para el taller del Hangar de Tame, la cual cuando se conectaban equipos para el taller consumía más amperaje y activaba el breaker de protección y habría el circuito por lo cual se apagaban las cámaras. El persona de ADC & HAS corta la derivación de la acometida y las cámaras quedan operativas.', 0 ); 
